// packageManagerSVGs maps lowercase package manager keys to their inline SVG strings.
// Keys mirror the mapping in IntelliJ's PackageManagerIconProvider.
var packageManagerSVGs = map[string]string{
	"gradle":         svgPMGradle,
	"maven":          svgPMMaven,
	"npm":            svgPMNpm,
	"pip":            svgPMPython,
	"rubygems":       svgPMRubyGems,
	"yarn":           svgPMYarn,
	"yarn-workspace": svgPMYarn,
	"sbt":            svgPMSbt,
	"golangdep":      svgPMGolangDep,
	"govendor":       svgPMGoVendor,
	"golang":         svgPMGolang,
	"gomodules":      svgPMGolang,
	"nuget":          svgPMNuget,
	"paket":          svgPMPaket,
	"composer":       svgPMComposer,
	"linux":          svgPMLinux,
	"deb":            svgPMDeb,
	"apk":            svgPMApk,
	"cocoapods":      svgPMCocoapods,
	"rpm":            svgPMRpm,
	"dockerfile":     svgPMDocker,
}

// PackageManagerSVG returns the inline SVG for the given OSS package manager key.
// Matching is case-insensitive. Returns "" for unrecognized keys, which signals the
// caller to fall back to the OS file icon.
func PackageManagerSVG(pm string) string {
	return packageManagerSVGs[strings.ToLower(pm)]
}
